package A_First_Project;

// Use square root function to find out the square root of  the given number...
// Takes and returns double value
import java.lang.Math;

public class SquareRoot {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double num =25;
		double ans;
		
		ans = Math.sqrt(num);
		
		System.out.println("The square root of the number " +num+ "is   ----> " +ans);
		
		

		
	}

}
